const alertButton=document.querySelector("#alertButton"),alertPopup=document.querySelector("#alertPopup");alertButton.addEventListener("click",()=>{"function"==typeof alertPopup.showModal&&alertPopup.showModal()}),alertPopup.addEventListener("click",t=>{var e=alertPopup.getBoundingClientRect();e.top<=t.clientY&&t.clientY<=e.top+e.height&&e.left<=t.clientX&&t.clientX<=e.left+e.width||alertPopup.close()});